mapper.update:
update doc set position = position -1 where parent_id = (select parent_id from (select parent_id from doc where id = #{destId})a)
  and position < (select position from (select position from doc where id = #{destId})b);
update doc set position = (select position from (select (select position from doc where id = #{destId})-1 position) position),
  parent_id = (select parent_id from (select parent_id from doc where id = #{destId}) parent_id) where id = #{id};

update doc set position = position +1 where parent_id = (select parent_id from (select parent_id from doc where id = #{destId})a)
  and position > (select position from (select position from doc where id = #{destId})b);
update doc set position = (select position from (select (select position from doc where id = #{destId})+1 position) position),
  parent_id = (select parent_id from (select parent_id from doc where id = #{destId}) parent_id) where id = #{id};

update doc set position =
  (select position from (select IFNULL((select min(position) from doc where parent_id = #{destId})-1, 888)position)position),
  parent_id = #{destId} where id = #{id};

mapper.select:
        SELECT T2.level, T3.*
        FROM(
                SELECT @codes as _ids,
			( SELECT @codes := GROUP_CONCAT(id)
				FROM doc
				 WHERE FIND_IN_SET(parent_id, @codes)
			) as T1,
		  @l := @l+1 as level
                FROM doc,
                    (SELECT @codes := #{id}, @l := -1 ) T4
                WHERE @codes IS NOT NULL
            ) T2, doc T3
        WHERE FIND_IN_SET(T3.id, T2._ids)
          and id != #{id}
        ORDER BY level, position
        ;
