mapper.select:
SELECT T2.level, T3.*
FROM(
        SELECT @codes as _ids,
               ( SELECT @codes := GROUP_CONCAT(id)
                 FROM project
                 WHERE FIND_IN_SET(parent_id, @codes)
               ) as T1,
               @l := @l+1 as level
        FROM project,
             (SELECT @codes := #{id}, @l := -1 ) T4
        WHERE @codes IS NOT NULL
    ) T2, project T3
WHERE FIND_IN_SET(T3.id, T2._ids)
  and id != #{id}
ORDER BY level, position
;

mapper.delete:
delete from project where id in (select id from (
    SELECT T3.id
    FROM(
            SELECT @codes as _ids,
                   ( SELECT @codes := GROUP_CONCAT(id)
                     FROM project
                     WHERE FIND_IN_SET(parent_id, @codes)
                   ) as T1,
                   @l := @l+1 as level
            FROM project,
                 (SELECT @codes := #{id}, @l := -1 ) T4
            WHERE @codes IS NOT NULL
        ) T2, project T3
    WHERE FIND_IN_SET(T3.id, T2._ids)
)id
)

mapper.update:
update project set position =
  (select position from (select IFNULL((select min(position) from project where parent_id = #{destId})-1, 888)position)position),
  parent_id = #{destId} where id = #{id};
